    public int minPathSum(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        int[][] num = new int[n][m];
        num[n-1][m-1] = grid[n-1][m-1];

        for(int i = n-1; i >= 0; i--){
            for(int j = m-1; j>= 0; j--){
                if (i + 1 < n && j + 1 < m) {
                    if(num[i + 1][j] > num[i][j + 1]){
                        num[i][j] = grid[i][j] + num[i][j + 1];
                    } else {
                        num[i][j] = grid[i][j] + num[i + 1][j];
                    }
                } else if (i + 1 < n) {
                    num[i][j] = num[i + 1][j]+grid[i][j];
                } else if (j + 1 < m) {
                    num[i][j] = num[i][j + 1]+grid[i][j];
                }
            }
        }

        return num[0][0];
    }
